const toggle=document.getElementById("theme-toggle"),icon=document.getElementById("theme-icon"),html=document.documentElement,sunIcon="/assets/images/icons/sun.png",moonIcon="/assets/images/icons/moon.png";toggle.addEventListener("click",()=>{const t=html.getAttribute("data-theme"),e=t==="dark"?"light":"dark";html.setAttribute("data-theme",e),localStorage.setItem("theme",e),icon.src=e==="dark"?moonIcon:sunIcon,icon.alt=e==="dark"?"dark theme is ON":"light theme is ON"});const initialTheme=html.getAttribute("data-theme");icon.src=initialTheme==="dark"?moonIcon:sunIcon,icon.alt=initialTheme==="dark"?"dark theme is ON":"light theme is ON"